import re
def clean_text(text):
    # Remove URLs
    text = re.sub(r'http\S+|www\S+|https\S+', '', text, flags=re.MULTILINE)
    
    # Remove emojis (Unicode range covers most emojis)
    text = re.sub(r'[\U0001F600-\U0001F64F'  # emoticons
                  r'\U0001F300-\U0001F5FF'  # symbols & pictographs
                  r'\U0001F680-\U0001F6FF'  # transport & map symbols
                  r'\U0001F700-\U0001F77F'  # alchemical symbols
                  r'\U0001F780-\U0001F7FF'  # Geometric Shapes Extended
                  r'\U0001F800-\U0001F8FF'  # Supplemental Arrows-C
                  r'\U0001F900-\U0001F9FF'  # Supplemental Symbols and Pictographs
                  r'\U0001FA00-\U0001FA6F'  # Chess Symbols
                  r'\U0001FA70-\U0001FAFF'  # Symbols and Pictographs Extended-A
                  r'\U00002700-\U000027BF'  # Dingbats
                  r'\U0001F1E0-\U0001F1FF'  # flags (iOS)
                  r']+', '', text)
    
    # Remove special symbols (e.g., #, @) and dashes
    text = re.sub(r'[^A-Za-z0-9\s]', '', text)
    
    # Remove extra spaces
    clean_text = re.sub(r'\s+', ' ', text).strip().lower()
    
    return clean_text


def predict_category(input_text):
    """
    Predicts the cyber crime category for a given text input using a pre-trained FastText model.
    
    Args:
        input_text (str): The text to classify
        
    Returns:
        tuple: (predicted_category, confidence_score)
    """
   
    
    # Define the category labels
    class_labels = [
        'any_other_cyber_crime',
        'cyberbullying_and_online_harassment',
        'financial_frauds',
        'system_hacking_and_damage'
    ]
    
    # Get prediction
    label, probability = model.predict(input_text)
    
    # Convert label to category
    label_index = int(label[0].replace('__label__',''))
    predicted_class = class_labels[label_index]
    confidence_score = probability[0]
    #print(predicted_class)
    return predicted_class, confidence_score


def predict_sub_category(text, context_parameters, detected_categories):
    """
    Analyze text and detect subcategories based on detected main categories.
    
    Args:
        text (str): Input text to analyze
        context_parameters (dict): Dictionary containing categories and keywords
        detected_categories (list/str): Main category/categories detected
        
    Returns:
        str: Comma-separated string of detected subcategories
    """
    # Convert text to lowercase for matching
    text = text.lower()
    
    # Handle if detected_categories is a string
    if isinstance(detected_categories, str):
        detected_categories = [detected_categories]
    
    detected_subcategories = set()
    
    for main_category in detected_categories:
        if main_category in context_parameters:
            subcategories = context_parameters[main_category]
            
            # Check each subcategory's keywords
            for subcategory, keywords in subcategories.items():
                for keyword in keywords:
                    # Convert keyword to lowercase and create word boundary pattern
                    keyword = keyword.lower()
                    pattern = r'\b' + re.escape(keyword) + r'\b'
                    
                    # If keyword found, add subcategory and break inner loop
                    if re.search(pattern, text):
                        detected_subcategories.add(subcategory)
                        break
    
    # Convert set to sorted list and join with commas
    return sorted(detected_subcategories)


def setup_nltk():
    """
    Set up NLTK data with proper error handling
    """
    # Create custom NLTK data directory in current directory
    nltk_data_dir = os.path.join(os.getcwd(), 'nltk_data')
    if not os.path.exists(nltk_data_dir):
        os.makedirs(nltk_data_dir)
    
    # Set NLTK data path
    nltk.data.path.append(nltk_data_dir)
    
    # Required NLTK downloads with error handling
    required_packages = ['punkt', 'wordnet', 'stopwords']
    
    for package in required_packages:
        try:
            nltk.download(package, download_dir=nltk_data_dir, quiet=True)
        except Exception as e:
            print(f"Error downloading {package}: {str(e)}")
            return False
    
    return True




def initialize_nlp_components():
    """
    Initialize NLP components with error handling
    """
    try:
        tokenizer = word_tokenize
        lemmatizer = WordNetLemmatizer()
        stop_words = set(stopwords.words('english'))
        return tokenizer, lemmatizer, stop_words
    except Exception as e:
        print(f"Error initializing NLP components: {str(e)}")
        return None, None, None

def preprocess_text(text, tokenizer, lemmatizer, stop_words):
    """
    Preprocess text with error handling
    """
    try:
        if isinstance(text, list):
            text = ' '.join(text)
        text = text.lower()
        tokens = tokenizer(text)
        filtered_tokens = [lemmatizer.lemmatize(word) 
                         for word in tokens 
                         if word not in stop_words and word.isalpha()]
        return " ".join(filtered_tokens)
    except Exception as e:
        print(f"Error in text preprocessing: {str(e)}")
        return text
    
def check_sub_category_lsa(category, incident_description, tokenizer, lemmatizer, stop_words, categories):
    """
    Find similar subcategory with error handling
    """
    try:
        subcategories = categories.get(category, {})
        if not subcategories:
            return "any_other_cyber_crime", None
        
        subcategory_descriptions = list(subcategories.values())
        subcategory_titles = list(subcategories.keys())
        
        preprocessed_descriptions = [preprocess_text(' '.join(desc), tokenizer, lemmatizer, stop_words) 
                                  for desc in subcategory_descriptions]
        preprocessed_incident = preprocess_text(incident_description, tokenizer, lemmatizer, stop_words)
        
        tfidf_vectorizer = TfidfVectorizer()
        tfidf_matrix_descriptions = tfidf_vectorizer.fit_transform(preprocessed_descriptions)
        tfidf_matrix_incident = tfidf_vectorizer.transform([preprocessed_incident])
        
        num_features = tfidf_matrix_descriptions.shape[1]
        n_components = min(100, num_features)
        lsa = TruncatedSVD(n_components=n_components, random_state=42)
        lsa_matrix_descriptions = lsa.fit_transform(tfidf_matrix_descriptions)
        lsa_matrix_incident = lsa.transform(tfidf_matrix_incident)
        
        cosine_similarities = cosine_similarity(lsa_matrix_incident, lsa_matrix_descriptions).flatten()
        most_similar_index = np.argmax(cosine_similarities)
        
        #print(most_similar_index)
        most_similar_subcategory = subcategory_titles[most_similar_index]
        similarity_score = cosine_similarities[most_similar_index]
        
        return most_similar_subcategory, similarity_score
    except Exception as e:
        print(f"Error finding similar subcategory: {str(e)}")
        return None, None

